const initialState = {
	books: [],
	loading: true,
	cartItems: [],
	totalOrder: 0,
};

const reducer = (state = initialState, action) => {
	switch (action.type) {
		case "BOOKS_LOADED":
			return {
				...state,
				books: action.payload,
				loading: false,
			};

		case "BOOKS_ADD_TO_CART":
			const cartBook = state.books.find(book => book.id === action.payload);
			const searchBook = state.cartItems.find(
				book => book.id === action.payload
			);

			if (searchBook) {
				return {
					...state,
					cartitems: state.cartItems.map(item => {
						return
					})
				};
			} else {
				const newCartBook = {
					id: cartBook.id,
					title: cartBook.title,
					price: cartBook.price,
					count: 0,
				};
				return {
					...state,
					cartItems: state.cartItems.concat(newCartBook),
				};
			}
		default:
			return state;
	}
};

export default reducer;
