const booksLoaded = (newBooks) => {
	if (newBooks === undefined) {
		throw new Error("Request failed: for books");
	} else {
		return {
			type: "BOOKS_LOADED",
			payload: newBooks,
		};
	}
};

const booksAddCart = id =>{
	return{
		type: "BOOKS_ADD_TO_CART",
	}
}

export { booksLoaded };
