const initialState = {
	books: [],
	loading: true,
	cartItems: [
		{
			id: 1,
			name: "book 1",
		},
	],
	totalOrder: 0,
};

const reducer = (state = initialState, action) => {
	switch (action.type) {
		case "BOOKS_LOADED":
			return {
				...state,
				books: action.payload,
				loading: false,
			};

		case "BOOKS_ADD_TO_CART":

			return {
				...state,
			};
		default:
			return state;
	}
};

export default reducer;
